package com.example.homework3_month3.fragment;

import android.animation.AnimatorSet;
import android.animation.ObjectAnimator;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.view.animation.Animation;
import android.view.animation.BounceInterpolator;
import android.view.animation.ScaleAnimation;
import android.widget.ImageView;

import com.bumptech.glide.Glide;
import com.example.homework3_month3.R;
import com.example.homework3_month3.databinding.FragmentCityDetailBinding;

public class CityDetailFragment extends Fragment {

    private FragmentCityDetailBinding binding;
    private boolean isAnimated = false;


    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        binding = FragmentCityDetailBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        String city = getArguments() != null ? getArguments().getString("City") : "City not specified";
        assert city != null;
        String cityImageUrl = getCityImageUrl(city);
        String description = getCityDescription(city);

        binding.tvCityName.setText(city);
        binding.tvCityDescription.setText(description);

        Glide.with(this).load(cityImageUrl).into(binding.imgCity);



        binding.imgCity.setOnClickListener(v -> {
            if (isAnimated) {
                resetAnimation(); // Сбрасываем анимацию
            } else {
                startAnimation(); // Запускаем анимацию
            }
        });

    }

    private void resetAnimation() {
        binding.imgCity.clearAnimation();
        binding.imgCity.setScaleX(1.0f);
        binding.imgCity.setScaleY(1.0f);

        binding.tvCityDescription.setVisibility(View.VISIBLE);
        binding.tvCityName.setVisibility(View.VISIBLE);

        isAnimated = false;
    }

    private void startAnimation() {
        ScaleAnimation scaleAnimation = new ScaleAnimation(
                1.0f, 1.5f, // Начальный и конечный масштаб по оси X
                1.0f, 1.5f, // Начальный и конечный масштаб по оси Y
                Animation.RELATIVE_TO_SELF, 0.5f, // Точка отсчета по X (центр)
                Animation.RELATIVE_TO_SELF, 0.5f  // Точка отсчета по Y (центр)
        );

        scaleAnimation.setDuration(500); // Длительность (500 миллисекунд)
        scaleAnimation.setFillAfter(true); // Сохранить конечное состояние анимации

        binding.imgCity.startAnimation(scaleAnimation);

        binding.tvCityDescription.setVisibility(View.GONE);
        binding.tvCityName.setVisibility(View.GONE);

        isAnimated = true;
    }
    
    


    private String getCityImageUrl(String city) {
        switch (city) {
            case "Lagos":
                return "data:image/jpeg;base64,";
            case "Beijing":
                return "https://example.com/beijing_image.jpg";
            // Добавить ссылки на фотки других городов
            default:
                return "https://example.com/default_city_image.jpg";
        }
    }

    private String getCityDescription(String city) {
        switch (city) {
            case "Lagos":
                return "Lagos is a major city in Nigeria known for its port and bustling markets.";
            case "Beijing":
                return "Beijing is the capital city of China, rich in history and culture.";
            // Добавить описания других городов сюда
            default:
                return "No description available.";
        }
    }
}